import React from 'react';
import './App.css';



class App extends React.Component{
  // constructor(props){
  //   super(props);
  //   this.state={SONGS:""};
  // }

  // callAPI(){
  //   fetch("http://localhost:9000/testAPI")
  //     .then(res => res.text())
  //     .then(res => this.setState({SONGS: res}));
  // }

  // componentWillMount(){
  //   this.callAPI();
  // }

  render(){
    return (
      <div className="App">
        <div className="header">
          <Header />
        </div>
        <div className="game-on">
          <Game />
        </div>
      </div>
  )
}
}


class Header extends React.Component {
  render() {
    return(
      <div classname="header">
        <h1>The Billboard Hot 100 GUESSING GAME</h1>
        <h2>Given three songs on the Billboard Hot 100, guess their relative ranking and see where YOU land on the scale from "Way 2 Sexy" ;) to "A-O-K" :/ to "Ain't Shit" >:(</h2>
      </div>
    );
  }
}

class Card extends React.Component {
  render() {
    return (
      <button className="card">
        {this.props.song}
      </button>
    );
  }
}



class Board extends React.Component {
  constructor(props){
    super(props);
    this.state = {songs:[]};
  }
  componentDidMount() {
    fetch("http://localhost:9000/testAPI")
      .then(res => res.text())
      .then(res => JSON.parse(res))
      .then(res => this.setState({songs: res}));
    // var s = fetch("http://localhost:9000/testAPI")
    //   .then(res => res.text())
    // var s2 = JSON.parse(s);
    // var s3 = [];
    // for(var i = 0; i < 100; i++) {
    //   s3.push(JSON.parse(s2[i]))
    // }
    // this.setState = {songs:s2}
  }
  renderCard(i) {
    var ind = Math.floor(Math.random()*this.state.songs.length);
    var song = this.state.songs[ind];
    return <Card song={song} rank={ind} id={i}/>;
  }
  render() {
    return(
      <div className="options">

      {this.renderCard(0)}
      {this.renderCard(1)}
      {this.renderCard(2)}
      </div>
    );
  }
}

class Game extends React.Component {

  render() {
    return (
      <div className="game">
        <div className="game-board">
          <Board />
        </div>
        <div className="game-info">
          <div>{/* status */}</div>
          <ol>{/* TODO */}</ol>
        </div>
      </div>
    );
  }
}


export default App;
