import React from 'react';
import './App.css';

class App extends React.Component{

  render(){
    return (
      <div className="App">
        <div className="header">
          <Header />
        </div>
        <div className="game-on">
          <Game />
        </div>
      </div>
  )
}
}

class Header extends React.Component {
  render() {
    return(
      <div classname="header">
        <h1>The Billboard Hot 100 GUESSING GAME</h1>
        <h2>Given three songs on the Billboard Hot 100, guess their relative ranking and see where YOU land on the scale from "Way 2 Sexy" ;) to "A-O-K" :/ to "Ain't Shit" >:(</h2>
      </div>
    );
  }
}

