// rewriting Card as function

function Card(props) {
  return (
    <div>
        <button className="card" onClick={props.onClick} >
          {props.song}
        </button>
        <h3>{props.guess}</h3>
    </div>
  );
}

function Submit(props) {
  if (!props.show) {
    return null;
  }
  return (
    <button className="submit" onClick={props.onClick} >
          SUBMIT
        </button>
  )
}

class Board extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      cards: Array(3).fill(null),
      submitIsOption: false,
    };
  }

handleGuess(i) {
  const cards = this.state.cards.slice();
  if (cards[i] !== 1) {
    cards[i] = 1;
    for (var j = 0; j < 3; j++) {
      if (i !== j && cards[j] && cards[j] < 3) {
        cards[j]++;
      }
    }
    var noEmpty = false;
    if (cards[0] && cards[1] && cards[2]) {noEmpty = true;}
    this.setState({cards: cards, submitIsOption: noEmpty})
  }
}

handleSubmit() {
  // change state of game somehow??
}

  renderCard(i) {
    return ( 
      <Card 
        song={this.props.songsToGuess[i].track} 
        rank={this.props.songsToGuess[i].rank} 
        id={i} 
        guess={this.state.cards[i]}
        onClick={() => this.handleGuess(i)}
        />
    );
  }

  render() {
    const status = 'Click the songs from last to first to rank them!'
    return(
      <div>
      <div className="status">{status}</div>
        <div className="options">
          {this.renderCard(0)}
          {this.renderCard(1)}
          {this.renderCard(2)}
          <Submit show={this.state.submitIsOption} onClick={() => this.handleSubmit()}/>
        </div>
      </div>
    );
  }
}


class Game extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      songs:[],
    };
  }
  componentDidMount() {
    fetch("http://localhost:9000/testAPI")
      .then(res => res.text())
      .then(res => JSON.parse(res))
      .then(res => this.setState({songs: res}));
  }

  renderBoard() {
    var threeSongs = [];
    for (var i = 0; i < 3; i++) {
      var ind = Math.floor(Math.random()*this.state.songs.length); //replace this.state.songs.length with 100
      var s = this.state.songs[ind];
      var song = {track: "", rank: -1};
      song.track = s;
      song.rank = ind;
      threeSongs.push(song);
    }
    return <Board songsToGuess = {threeSongs}/>;
  }

  render() {
    return (
      <div className="game">
        <div className="game-board">
          {this.renderBoard()}
        </div>
        <div className="game-info">
          <div>{/* status */}</div>
          <ol>{/* TODO */}</ol>
        </div>
      </div>
    );
  }
}

export default App;
