import React from 'react';
import './App.css';

class App extends React.Component{

  render(){
    return (
      <div className="App">
        <div className="header">
          <Header />
        </div>
        <div className="game-on">
          <Game />
        </div>
      </div>
  )
}
}

class Header extends React.Component {
  render() {
    return(
      <div classname="header">
        <h1>The Billboard Hot 100 GUESSING GAME</h1>
        <h2>Given three songs on the Billboard Hot 100, guess their relative ranking and see where YOU land on the scale from "Way 2 Sexy" ;) to "A-O-K" :/ to "Ain't Shit" >:(</h2>
      </div>
    );
  }
}

function Card(props) {
  return (
    <div>
        <button className="card" onClick={props.onClick} >
          {props.song}
        </button>
        <h3>{props.guess} </h3>
        {props.revealRank && <h3> REAL RANK -- {props.rank}</h3>}
    </div>
  );
}

function Submit(props) {
  if (!props.show) {
    return null;
  }
  return (
    <button className="submit" onClick={props.onClick} >
          SUBMIT
        </button>
  )
}

class Board extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      cards: Array(3).fill(null),
      submitIsOption: false,
      numCorrect: 0,
      gameOver: false,
    };
  }

handleGuess(i) {
  if (!gameOver) {
    const cards = this.state.cards.slice();
    if (cards[i] !== 1) {
      cards[i] = 1;
      for (var j = 0; j < 3; j++) {
        if (i !== j && cards[j] && cards[j] < 3) {
          cards[j]++;
        }
      }
      var noEmpty = false;
      if (cards[0] && cards[1] && cards[2]) {noEmpty = true;}
      this.setState({cards: cards, submitIsOption: noEmpty})
    }
  }
}

handleSubmit() {
  var guess1 = this.state.cards.find(element => element === 1);
  var guess2 = this.state.cards.find(element => element === 2);
  var guess3 = this.state.cards.find(element => element === 3);
  var ranks = Object.values(this.props.songsToGuess);
  var song1 = ranks[guess1];
  var song2 = ranks[guess2];
  var song3 = ranks[guess3];
  var guess1Correct = true ? song1 <= song2 && song1 <= song3 : false;
  var guess3correct = true ? song3 >= song1 && song3 >= song2 : false;
  if (guess1Correct && guess3correct) {
    this.setState({ numCorrect: 3 });
  } else if (guess1Correct || guess3correct) {
    this.setState({ numCorrect: 1});
  }
  this.setState({gameOver: true});
}

  renderCard(i) {
    return ( 
      <Card 
        song={this.props.songsToGuess[i].track} 
        rank={this.props.songsToGuess[i].rank} 
        id={i} 
        guess={this.state.cards[i]}
        onClick={() => this.handleGuess(i)}
        revealRank={this.state.gameOver}
        />
    );
  }

  render() {
    const status = 'Click the songs from last to first to rank them!' + (this.state.submitIsOption ? ' Press submit when you\'re ready to check your answers!' : '');
    return(
      <div>
      <div className="status">{status}</div>
        <div className="options">
          {this.renderCard(0)}
          {this.renderCard(1)}
          {this.renderCard(2)}
          <Submit show={this.state.submitIsOption} onClick={() => this.handleSubmit()}/>
          {this.state.gameOver &&
            <h3>You got {this.state.numCorrect} right!</h3>
          }
        </div>
      </div>
    );
  }
}


class Game extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      songs:[],
      curSongs:[],
      totalRight: 0,
      gameOver: false,
    };
  }
  componentDidMount() {
    fetch("http://localhost:9000/testAPI")
      .then(res => res.text())
      .then(res => JSON.parse(res))
      .then(res => this.setState({songs: res}));
  }

  renderBoard() {
    var threeSongs = [];
    for (var i = 0; i < 3; i++) {
      var ind = Math.floor(Math.random()*this.state.songs.length);
      var s = this.state.songs[ind];
      var song = {track: "", rank: -1};
      song.track = s;
      song.rank = ind;
      threeSongs.push(song);
    }
    return <Board songsToGuess = {threeSongs} />;
  }

  render() {
    return (
      <div className="game">
        {this.renderBoard()}
      </div>
    );
  }
}

export default App;
