import React from 'react';
import './App.css';

class App extends React.Component{

  render(){
    return (
      <div className="App">
        <div className="header">
          <Header />
        </div>
        <div className="game-on">
          <Game />
        </div>
      </div>
  )
}
}

class Header extends React.Component {
  render() {
    return(
      <div classname="header">
        <h1>The Billboard Hot 100 GUESSING GAME</h1>
        <h2>Given three songs on the Billboard Hot 100, guess their relative ranking and see where YOU land on the scale from "Way 2 Sexy" ;) to "A-O-K" :/ to "Ain't Shit" >:(</h2>
      </div>
    );
  }
}

class Card extends React.Component {
  render() {
    return (
      <div>
        <button 
          className="card" 
          onClick={() => this.props.onClick()}
        >
          {this.props.song}
        </button>
        <h3>{this.props.guess}</h3>
      </div>
    );
  }
}

class Board extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      cards: Array(3).fill(null),
    };
  }

handleClick(i) {
  const cards = this.state.cards.slice();
  if (cards[i] !== 1) {
    cards[i] = 1;
    for (var j = 0; j < 3; j++) {
      if (i !== j && cards[j] && cards[j] < 3) {
        cards[j]++;
      }
    }
    this.setState({cards: cards})
  }
}

  renderCard(i) {
    return ( 
      <Card 
        song = {"poop"}
        rank = {69}
        // song={this.props.songsToGuess[i].track} 
        // rank={this.props.songsToGuess[i].rank} 
        id={i} 
        guess={this.state.cards[i]}
        onClick={() => this.handleClick(i)}
        />
    );
  }

  render() {
    const status = 'Click to rank the songs!'
    return(
      <div>
      <div className="status">{status}</div>
        <div className="options">
          {this.renderCard(0)}
          {this.renderCard(1)}
          {this.renderCard(2)}
        </div>
      </div>
    );
  }
}


class Game extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      songs:[],
    };
  }
  componentDidMount() {
    fetch("http://localhost:9000/testAPI")
      .then(res => res.text())
      .then(res => JSON.parse(res))
      .then(res => this.setState({songs: res}));
  }

  renderBoard() {
    var threeSongs = {};
    for (var i = 0; i < 3; i++) {
      var ind = Math.floor(Math.random()*this.state.songs.length); //replace this.state.songs.length with 100
      var s = this.state.songs[ind];
      var song = {track: "", rank: -1};
      song.track = s;
      song.rank = ind;
      threeSongs.push(song);
    }
    return <Board songsToGuess = {threeSongs}/>;
  }

  render() {
    return (
      <div className="game">
        <div className="game-board">
          {this.renderBoard()}
        </div>
        <div className="game-info">
          <div>{/* status */}</div>
          <ol>{/* TODO */}</ol>
        </div>
      </div>
    );
  }
}

export default App;
