import React from 'react';
import logo from './logo.svg';
import './App.css';




class App extends React.Component{
  constructor(props){
    super(props);
    this.state={SONGS:""};
  }

  callAPI(){
    fetch("http://localhost:9000/testAPI")
      .then(res => res.text())
      .then(res => this.setState({SONGS: res}));
  }

  componentWillMount(){
    this.callAPI();
  }

  render(){
    return (
      <div className="App">
        <div className="header">
          <Header />
        </div>
        <div className="game-on">
          <Game SONGS = {this.state.SONGS}/>
        </div>
      {/* <p>{this.state.SONGS}</p> */}
      </div>
  )
}
}


class Header extends React.Component {
  render() {
    return(
      <div classname="header">
        <h1>The Billboard Hot 100 GUESSING GAME</h1>
        <h2>Given three songs on the Billboard Hot 100, guess their relative ranking and see where on the scale you rank from "Way 2 Sexy" to "A-O-K" to "Ain't Shit"</h2>
      </div>
    );
  }
}

class Card extends React.Component {
  render() {
    return (
      <button className="card">
        {this.props.song} {this.props.artist} {this.props.rank} {this.props.id}
      </button>
    );
  }
}

class Board extends React.Component {
  renderCard(i) {
    var ind = Math.floor(Math.random()*this.props.SONGS.length);
    var track = this.props.SONGS[ind];
    this.props.SONGS.splice(ind, 1);
    return <Card song={track[0]} artist={track[1]} rank={ind} id={i}/>;
  }
  render() {
    return(
      <div className="options">
      {this.renderCard(0)}
      {this.renderCard(1)}
      {this.renderCard(2)}
      </div>
    );
  }
}

class Game extends React.Component {

  render() {
    return (
      <div className="game">
        <div className="game-board">
          <Board SONGS = {this.props.SONGS}/>
        </div>
        <div className="game-info">
          <div>{/* status */}</div>
          <ol>{/* TODO */}</ol>
        </div>
      </div>
    );
  }
}


export default App;
