import React from 'react';
import './App.css';

class App extends React.Component{

  render(){
    return (
      <div className="App">
        <div className="header">
          <Header />
        </div>
        <div className="game-on">
          <Game />
        </div>
      </div>
  )
}
}


class Header extends React.Component {
  render() {
    return(
      <div classname="header">
        <h1>The Billboard Hot 100 GUESSING GAME</h1>
        <h2>Given three songs on the Billboard Hot 100, guess their relative ranking and see where YOU land on the scale from "Way 2 Sexy" ;) to "A-O-K" :/ to "Ain't Shit" :,(</h2>
      </div>
    );
  }
}

class Card extends React.Component {
  render() {
    return (
      <button className="card">
        {this.props.song} {this.props.rank}
      </button>
    );
  }
}

class Board extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      cards: Array(3).fill(null),
    };
  }
  renderCard(i) {
    var ind = Math.floor(Math.random()*this.props.songs.length);
    var track = this.props.songs[ind].titleArtist;
    // gotta figure out how to separate title and artist -
    return <Card song={track} rank={ind} id={i}/>;
  }
  render() {
    return(
      <div className="options">
      {this.renderCard(0)}
      {this.renderCard(1)}
      {this.renderCard(2)}
      </div>
    );
  }
}

class Game extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      songs:[],
    };
  }
  componentDidMount() {
    fetch("http://localhost:9000/testAPI")
      .then(res => res.text())
      .then(res => JSON.parse(JSON.parse(res)))
      .then(res => this.setState({songs: res}));
  }
  render() {
    return (
      <div className="game">
        <div className="game-board">
          <Board songs = {this.state.songs}/>
        </div>
        <div className="game-info">
          <div>{/* status */}</div>
          <ol>{/* TODO */}</ol>
        </div>
      </div>
    );
  }
}


export default App;
